svg.height:
8*::before,
*::after {
  box-sizing: border-box;
}
* {
  margin: 0;
}

body {
  line-height: 1.5;
  color: white;
  background-color: #0f172a;
  position: relative;
}

img,
picture,
video,
canvas,
svg {
  display: block;
  max-width: 100%;
}
img {
  width: 200px;
  height: 200px;
}

nav {
  overflow: hidden;
  display: flex;
  gap: 20px;
  justify-content: center;
  margin: 60px 20px 30px 0px;
}

nav button.active {
}

button {
  font-size: 1.4rem;
  border: none;
  background: none;
  color: #f1f5f9;
}

button::after {
  display: block;
  content: '';
  border-bottom: solid 3px #f8fafc;
  transform: scaleX(0);
  transition: transform 250ms ease-in-out;
}

button:hover::after {
  transform: scaleX(1.0);

}

button:hover{
  cursor: pointer;
}

#content {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}

#logo {
  position: absolute;
  margin-right: 400px;
  fill: #f43f5e;
  width: 100px;
  height: 100px;

}

